const initialState = {
  indexOfCurrentRecord: '',
  imageArray: []
}
export const indexReducer = (state = {indexOfCurrentRecord: ''}, action) => {
  switch(action.type){
    case 'UPDATE_INDEX':
      return {
        ...state,
        indexOfCurrentRecord: action.payload
      }
    default:
      return state
  }
}
export const variableReducer = (state = initialState, action) => {
  switch(action.type){
    case 'UPDATE_INDEX':
      return {
        ...state,
        indexOfCurrentRecord: action.payload
      }
    default:
      return state
  }
}
