import React from 'react';
import Record from './Record';

let type = "master";

const presentResults = (results, type) => {
  if(!results){
    return;
  } else {
    let filtered = results.filter(result => result.type !== type)
    return filtered.map((record, index) => {
      console.log(record.type, record)
      return <Record 
        key={record.id}
        index={index}
        title={record.title} 
        year={record.year}/>
    })
  }
}

function SearchResults({results, sort, onAdd}) {
  return(
    <div className="searchResults">
      <div className="sortBy">
        <label htmlFor="">show:</label>
      <button value="" onClick={(e) => sort(e.target.value)}>All</button>
      <button value="artist" onClick={(e) => sort(e.target.value)}>Artists</button>
      <button value="release" onClick={(e) => sort(e.target.value)}>Records</button>
      </div>

      <div className="Wrapper">
        {presentResults(results, type)} 
      </div>
    </div>
  )
}

export default SearchResults;
