import React, {Fragment} from 'react';

function Autofill ({term, array}) {
  console.log(array);
  //find match
  if(!term){ return []};

  let suggestions = array.filter(
    string => string.toLowerCase().indexOf(term.toLowerCase()) > -1);
/*   const highlightSuggestion = (term, suggestion) => {

  } */

  const returnSuggestions = () => {

    return suggestions.map(suggestion => (
    <p>{suggestion}</p>
    ))
  }

  return(
    <Fragment>
      {returnSuggestions()}
    </Fragment>
  )

}

export default Autofill;
