const initialState = {
  title: '',
  artist: '',
  year: '',
  label: '',
  size: ''
}

const recordReducer = (state = initialState, action) => {
  switch(action.type){
    case 'CHANGE_TITLE':
     return state.title = action.payload;
    case 'CHANGE_ARTIST':
     return state.title = action.payload;
    case 'CHANGE_YEAR':
     return state.title = action.payload;
    case 'CHANGE_TITLE':
     return state.title = action.payload;
    case 'CHANGE_TITLE':
     return state.title = action.payload;
    default: return state
  }
}

export default recordReducer;
