import React from 'react';

function Record ({title, artist, year, label, size, index, onRemove, img}) {

  let pic = img;
  if(pic == "https://img.discogs.com/4e860780db672a6b0038e39cd4613557f36e7df8/images/spacer.gif"){
    pic = './norecord.png';
  }
  const Remove = () => {
    onRemove(index);
  }

  return(
    <div className="Record" >
      <img src={pic} alt="album_cover"/>
      <div className="infoWrapper">
      <h2>{title}</h2>
      <h2>{artist}</h2>
      <p>{year}</p>
      <p>{label}</p>
      <p>{size}"</p>
      <button onClick={() => Remove(index)}>x</button>
    </div>

    </div>
  )
}

export default Record;
