const initialState = {
  records: [
    {
      title:"Chocolate & Cheese",
      artistID: 1,
      labelID: 3,
      year: 2000,
      size: 12
    },
    {
      title:"Quebec",
      artistID: 1,
      labelID: 3,
      year: 2005,
      size: 12
    },
    {
      title:"Selftitled",
      artistID: 2,
      labelID: 1,
      year: 2005,
      size: 12
    }
  ],
  artists: [
    {
      name: "Ween",
      artistID: 1
    },
    {
      name: "AUS",
      artistID: 2
  }],
  labels: [
    {
      name: "static shock",
      labelID: 1
    },
    {
      name: "noise solution",
      labelID: 2
    },
    {
      name: "weenRecs",
      labelID: 3
    }

  ]
}

 const collectionReducer = ( state = initialState, action) =>{
  switch(action.type){
    case 'ADD_RECORD':
      return {
        ...state, 
        records: state.records.pushaction.payload
      }
    case 'REMOVE_RECORD':
      return {
        ...state, 
        records: state.records.splice(action.payload, 1)
      }
    default:
      return state; 
  }
};

export default collectionReducer;
