import React, { useState} from 'react';
import Searchbar from './SearchBar';
import Editor from './Editor';
import Collection from './Collection';
import SearchResults from './SearchResults';
import Discogs from './util/Discogs';
import './App.css';

import { useSelector, useDispatch } from 'react-redux';
import { addNewRecord, removeRecord } from '../actions'



function App() {
  const records = useSelector(state => state.collection.records)
  const labels = useSelector(state => state.collection.labels)
  const artists = useSelector(state => state.collection.artists)
  const dispatch = useDispatch();

  const [search, setSearch] = useState([]);
  const [type, setType] = useState("release_title");
  const [hidden, setHidden] = useState({display: 'none'});
  const [cause, setCause] = useState("edit");

  const [version, setVersion] = useState(
    {
    header: 'Add Record',
    button: "Add"
    }
  )


  const handleSearch = (term, type) => {
    Discogs.search(term, type).then(response => setSearch(response.results));
  }

  const sortBy = (interest) => {
      setType(interest)
  }

  const addRecord = (artist, title, year, label, size) => {

    let artistID;
    let artistObj = artists.find(inListArtist => inListArtist.name === artist);
    if(artistObj){
      artistID = artistObj.artistID;
    }

    if(!artistID){
      artistID = Math.floor(Math.random()*10*1000);

      const artistsList = [...artists, {
        name: artist,
        artistID: artistID
      }];


    }

    let labelID;
    let labelObj = labels.find(inListLabel => inListLabel.name === label);
    if(labelObj){
      labelID = labelObj.labelID;
    }

    if(!labelID){
      labelID = Math.floor(Math.random()*10*1000);

      const labelsList = [...labels, {
        name: label,
        labelID: labelID
      }];

    }
    dis

    const recordToAdd = {
      artistID,
      title,
      year,
      size,
      labelID
    };

    dispatch(addNewRecord(recordToAdd));
  }

  const removeRecord = index => {
    dispatch(removeRecord(index));
  }


  const passForSuggestions = (array, topic) => {
    let suggestions = [];
    array.map(element => suggestions.push(element[topic]));
    return suggestions;
  }


  const changeVersion = () => {
    if(cause === "add") {
      setVersion(
        {
          header: 'Add Record',
          button: "Add"
        }
      )
    } else {
      setVersion(
        {
          header: 'Edit Record',
          button: "Save Changes"
        }
      )
    }
  };


  const showEditor = (ed_add) => {

   setCause(ed_add);
   changeVersion();
   setHidden({ display: "flex" });
  }


  return (
    <div className="App">
      <Searchbar 
        searchFor={type}
        onSearch={handleSearch}
        showEditor={showEditor}
        />
      <Editor
        index = {cause}
        version={version}
        style={hidden}
        setHidden={setHidden}
        onAdd={addRecord} 
        onRemove={removeRecord}
        titles={passForSuggestions(records, "title")}
        artists={passForSuggestions(artists, "name")}
        labels={passForSuggestions(labels, "name")}/>
      <SearchResults 
        isHidden={hidden} 
        results={search} 
        onAdd={addRecord}
        sort={sortBy}/>
      <Collection 
        showEditor={showEditor}
        labels={labels}
        artists={artists}
        records={records}
        removeRecord={removeRecord}/>
    </div>
  );
}

export default App;
