import React, { useState} from 'react';
import Searchbar from './SearchBar';
import Collection from './Collection'
import SearchResults from './SearchResults';
import Adder from './Adder';
import Discogs from './util/Discogs'
import './App.css';


function App() {

  const [search, setSearch] = useState([]);
  const [type, setType] = useState("release_title");
  const [hidden, setHidden] = useState("true");

  const [records, setRecords] = useState([
    {
      title:"Chocolate & Cheese",
      artistID: 1,
      label: "Ween Records",
      year: 2000,
      size: 12
    },
    {
      title:"Quebec",
      artistID: 1,
      label: "Ween Records",
      year: 2005,
      size: 12
    },
    {
      title:"Selftitled",
      artistID: 2,
      label: "Static Shock",
      year: 2005,
      size: 12
    },

  ]);
  const [artists, setArtists] = useState([
    {
      name: "Ween",
      id: 1
    },
    {
      name: "AUS",
      id: 2
    }]);
  const [labels, setLabels] = useState([]);

  const handleSearch = (term, type) => {
    hide("true");
    Discogs.search(term, type).then(response => setSearch(response.results));
  }

  const sortBy = (interest) => {
      console.log('sortBy')
      setType(interest)
  }

  const addRecord = (artist, title, year, label, size) => {

    let artistID = artists.find(inListArtist => inListArtist.name === artist)

    if(!artistID){
      artistID = Math.floor(Math.random()*10*1000);
      console.log(artistID);

      const artistsList = [...artists, {
        name: artist,
        artistID: artistID
      }];

      setArtists(artistsList);
    }

    const recordList = [...records, {
      artistID,
      title,
      year,
      size
    }];
    setRecords(recordList);

  };

  const removeRecord = index => {
    const recordList = [...records];
    recordList.splice(index, 1);
    setRecords(recordList);

  };

  const hide = (bool) => {
    setHidden(bool);
  }

  return (
    <div className="App">
      <Searchbar 
        searchFor={type}
        onSearch={handleSearch}
        />
      <Adder onAdd={addRecord} />
      <SearchResults 
        isHidden={hidden} 
        results={search} 
        onAdd={addRecord}
        sort={sortBy}/>
      <Collection 
        records={records}
        removeRecord={removeRecord}/>
    </div>
  );
}

export default App;
