import React, {Fragment} from 'react';

function Autofill ({term, array, setTerm}) {

  //find match
  if(!term){ return []};
  let suggestions = array.filter(
    string => string.toLowerCase().indexOf(term.toLowerCase()) > -1);


  const returnSuggestions = () => {


    return suggestions.map(suggestion => (
    <p className="suggestion" onClick={()=>setTerm(suggestion)}>{suggestion}</p>
    ))
  }

  return(
    <Fragment>
      {returnSuggestions()}
    </Fragment>
  )

}

export default Autofill;
