import React, { useState} from 'react';
import Searchbar from './SearchBar';
import Collection from './Collection'
import SearchResults from './SearchResults';
import './App.css';


function App() {
  const [records, setRecords] = useState([
    { artist: "artist1",
      title: "title1",
      year: 2020,
      label: "UrsiRecs"
    },
    { artist: "artist2",
      title: "title2",
      year: 2010,
      label: "UrsiShrecs"
    },
    { artist: "artist3",
      title: "title3",
      year: 2022,
      label: "Records"
    },
  ]);

  const addRecord = (artist, title, year, label) => {
    const recordList = [...records, {
      artist,
      title,
      year,
      label
    }];
    setRecords(recordList);
  };

  const removeRecord = index => {
    const recordList = [...records];
    recordList.splice(index, 1);
    setRecords(recordList);

  };

  return (
    <div className="App">
      <Searchbar 
      addRecord={addRecord}
      />
      <SearchResults />
      <Collection 
      records={records}
      removeRecord={removeRecord}/>
    </div>
  );
}

export default App;
