export const indexReducer = (state = {indexOfCurrentRecord: ''}, action) => {
  switch(action.type){
    case 'UPDATE_INDEX':
      return {
        ...state,
        indexOfCurrentRecord: action.payload
      }
      default:
        return state
      }
    }

export const updateImageReducer = (state = {imageArray : []}, action) => {
  switch(action.type){
    case 'UPDATE_IMAGE_ARRAY':
      return {
        imageArray: action.payload
      }
      default:
        return state
    }
  }
