import React from 'react';
import Record from './Record';


const displayArtist = (id, artists) => {
  let currentArtist = artists.find(artist => artist.artistID === id)
  return currentArtist.name;
}

function Collection ({records, removeRecord, artists}){

  return(
    <div className="Collection">
      {records.map((record, index ) => (
        <Record 
          key={index}
          artist={() => displayArtist(record.artistID, artists)}
          title={record.title}
          year={record.year}
          label={record.label}
          size={record.size}
          onRemove={removeRecord}
        />
      ))}
    </div>
  )
}
export default Collection;
