/* eslint-disable default-case */
import React from 'react';
import InputPlusAutofill from './Autofill';
import PicSelector from './PicSelector';
import { useSelector, useDispatch } from 'react-redux';
import { removeRecord, changeArtist, changeLabel, changeSize, changeTitle, changeYear, addNewRecord, addNewLabel, addNewArtist , hideEditor } from '../actions';


function Editor(){

  const dispatch = useDispatch();

  const records = useSelector(state => state.collection.records);
  const artists = useSelector(state => state.collection.artists);
  const labels = useSelector(state => state.collection.labels);

  const recordInProg = useSelector(state => state.editor.recordInProgress);


  const index = useSelector(state => state.currentIndex);
  const version = useSelector(state => state.add);

  const labelNames = labels.map(label => label.name);
  const artistNames = artists.map(artist => artist.name);
  const recordNames = records.map(record => record.title);



  const addItem = e => {

    e.preventDefault();

    if(!recordInProg.title || !recordInProg.artist){
      alert('Title or Artist Missing');
      return;
    }


    if(index !== undefined){
      console.log(index);
      dispatch(removeRecord(index));
    }

    let artistID;
    let artistObj = artists.find(inListArtist => inListArtist.name === recordInProg.artist);
    if(artistObj){
      artistID = artistObj.artistID;
    }

    if(!artistID){
      artistID = Math.floor(Math.random()*10*1000);
      const artistToAdd = {
        name: recordInProg.artist,
        artistID: artistID
      };
      dispatch(addNewArtist(artistToAdd))
    }


    let labelID;
    let labelObj = labels.find(inListLabel => inListLabel.name === recordInProg.label);
    if(labelObj){
      labelID = labelObj.labelID;
    }

    if(!labelID){
      labelID = Math.floor(Math.random()*10*1000);
      const labelToAdd = {
        name: recordInProg.label,
        labelID: labelID
      };
      dispatch(addNewLabel(labelToAdd))
    }


    const recordToAdd = {
      artistID,
      title: recordInProg.title,
      year: recordInProg.year,
      size: recordInProg.size,
      labelID: recordInProg.labelID,
      cover_image: recordInProg.cover
    };
    dispatch(addNewRecord(recordToAdd));
    dispatch(hideEditor());
  }

  return (
    <div className="Editor">
      <div className="inputBox">
      <h2 className="EditorHeader" > {version + ' record'}</h2>
        <InputPlusAutofill
          className="inputWrapper"
          term={recordInProg.title}
          list={recordNames} 
          fromTop={70}
          placeholder="Title"
          handleChange={newValue => {console.log(newValue);dispatch(changeTitle(newValue))}}
          />
        <InputPlusAutofill
          className="inputWrapper"
          term={recordInProg.artist}
          list={artistNames} 
          fromTop={100}
          placeholder="Artist"
          handleChange={newValue => dispatch(changeArtist(newValue))}
          />
        <input
          className="inputWrapper" 
          type="number"
          name="addYear"
          value={recordInProg.year}
          style={{top: '160px'}}
          onChange={e => dispatch(changeYear(e.target.value))}
          placeholder="Year"/> 
        <InputPlusAutofill
          className="inputWrapper"
          term={recordInProg.label}
          fromTop={130}
          list={labelNames} 
          placeholder="Label"
          handleChange={newValue => dispatch(changeLabel(newValue))}
          />
        <div 
          className="inputWrapper"
          style={{top: '190px'}}>
          <select 
            name="addSize" 
            value={recordInProg.size}

            onChange={e => dispatch(changeSize(e.target.value))}>
            <option value="12">12"</option>
            <option value="10">10"</option>
            <option value="7">7"</option>
          </select>
        </div>
      </div>
      <PicSelector />
      <div className="footer">
        <button 
        onClick={e => addItem(e)}
        type="submit" >{version}</button>
        <button onClick={() => dispatch(hideEditor())}>✖︎</button>
      </div>
    </div>
  )
}

export default Editor;
