import React from 'react';
import Discogs from './util/Discogs';
import {useSelector} from 'react-redux';


const findImage = (title, artist) => {

  let titleQuery = title.replace(/ /g, '+');
  let artistQuery = artist.replace(/ /g, '+');
  let term = `${titleQuery}+${artistQuery}`;
  let imageArray= [];
   Discogs.search(term).then(res => res.results.map(result => imageArray.push(result.cover_image)));
  console.log(imageArray)
}


function PicSelector () {

  const title = useSelector(state => state.record.title);
  const artist = useSelector(state => state.record.artist);
  const image = useSelector(state => state.record.cover_image);


  return(
    <div className="imgBox oneLine">
      <button 
      //submits editor why???
        onClick={() => findImage(title, artist)}
        id="findBtn">Find Cover</button>
      <button className="imageSwitches" id="prevBtn">prev</button>
      <button className="imageSwitches" id="nextBtn">next</button>
      <img className="previewImage" src={image} alt="album_cover" />
      <input 
        id="imageGetter" 
        type="text"
        placeholder="or insert imageUrl"/>
      <button id="imageGetterBtn"> upload</button>  
    </div>
  )
}

export default PicSelector;
