/* eslint-disable default-case */
import React, { useState, useEffect } from 'react';
import Autofill from './Autofill';
import { useSelector, useDispatch } from 'react-redux';
import { removeRecord } from '../actions';

function Editor({onAdd, index, style, version, setHidden}){

  const dispatch = useDispatch();

  const records = useSelector(state => state.collection.records);
  const artists = useSelector(state => state.collection.artists);
  const labels = useSelector(state => state.collection.labels);


  const labelNames = labels.map(label => label.name);
  const artistNames = artists.map(artist => artist.name);
  const recordNames = records.map(record => record.title);


  const title = useSelector(state => state.currentRecord.title);
/*   const [title, setTitle] = useState("");
  const [artist, setArtist] = useState("");
  const [year, setYear] = useState("");
  const [label, setLabel] = useState("");
  const [size, setSize] = useState("12"); */



  const updateAutofill= () => {
    if(typeof index === 'number'){
      setTitle(records[index].title);
      setYear(records[index].year);
      setSize(records[index].size);
      setArtist(artists.filter(artist => artist.artistID === records[index].artistID)[0].name);
      setLabel(labels.filter(label => label.labelID === records[index].labelID)[0].name);
    }
  }


  const handleChange = (e) => {
    switch(e.target.name){
      case "addTitle":
      setTitle(e.target.value)
      break;
      case "addArtist":
      setArtist(e.target.value);
      break;
      case "addYear":
      setYear(e.target.value);
      break;
      case "addLabel":
      setLabel(e.target.value);
      break;
      case "addSize":
      setSize(e.target.value);
      break;
    }
  }


  const addItem = e => {

    if(index){
      console.log("delete")
      dispatch(removeRecord(index));
    }
    e.preventDefault();

    onAdd(artist,title, year, label, size);
    setHidden( {display: "none" });
  }



  return(
    <div>
      <form className="Editor"
            style={style}
            onSubmit={e => addItem(e)}>
        <h2> {version.header}</h2>
        <div className="inputWrapper">
          <input 
            type="text"
            name="addTitle"
            value={title}
            onChange={e => handleChange(e)}
            placeholder="Title"/>  
          <Autofill
            className="Autofill"
            term={title}
            array={recordNames} 
            setTerm={setTitle}/>
        </div>
        <div className="inputWrapper">
          <input 
            type="text"
            name="addArtist"
            value={artist}
            onChange={e => handleChange(e)}
            placeholder="Artist"/> 
             <Autofill
             className="Autofill"
             term={artist}
             array={artistNames} 
             setTerm={setArtist}/>
          </div>
        <div className="inputWrapper">
          <input 
            type="number"
            name="addYear"
            value={year}
            onChange={e => handleChange(e)}
            placeholder="Year"/> 
          </div>
        <div className="inputWrapper">
          <input 
            type="text"
            name="addLabel"
            value={label}
            onChange={e => handleChange(e)}
            placeholder="Label"/> 
          <Autofill
            className="Autofill"
            term={label}
            array={labelNames} 
            setTerm={setLabel}/>
        </div>
        <div className="inputWrapper">
          <select 
            name="addSize" 
            value={size}
            onChange={e => handleChange(e)}>
            <option value="12">12"</option>
            <option value="10">10"</option>
            <option value="7">7"</option>
          </select>
        </div>
        <div className="inputWrapper">
  <button 
  onSubmit={addItem}
  type="submit" >{version.button}</button>
  <button onClick={() => setHidden( {display: "none" })}>✖︎</button>
        </div>
      </form>
    </div>
  )
}

export default Editor;
