import React, { useState} from 'react';
import Searchbar from './SearchBar';
import Collection from './Collection'
import SearchResults from './SearchResults';
import Adder from './Adder';
import Discogs from './util/Discogs'
import './App.css';


function App() {

  const [search, setSearch] = useState([]);
  const [type, setType] = useState("release_title");
  const [hidden, setHidden] = useState("true");

  const [records, setRecords] = useState([
    {
      title:"Chocolate"
    }
  ]);
  const [artists, setArtist] = useState([
    {
      name: "Ween",
      id: 1
    },
    {
      name: "AUS",
      id: 2
    }]);
  const [labels, setLabel] = useState([]);

  const handleSearch = (term, type) => {
    hide("true");
    Discogs.search(term, type).then(response => setSearch(response.results));
  }

  const sortBy = (interest) => {
      console.log('sortBy')
      setType(interest)
  }

  const addRecord = (artist, title, year, label, size) => {
    if(artists.some(inListArtist => inListArtist === artist)){
      let artistID = inListArtist.id
    }


    const recordList = [...records, {
      artist,
      title,
      year,
      label,
      size
    }];
    setRecords(recordList);
  };

  const removeRecord = index => {
    const recordList = [...records];
    recordList.splice(index, 1);
    setRecords(recordList);

  };

  const hide = (bool) => {
    setHidden(bool);
  }

  return (
    <div className="App">
      <Searchbar 
        searchFor={type}
        onSearch={handleSearch}
        />
      <Adder onAdd={addRecord} />
      <SearchResults 
        isHidden={hidden} 
        results={search} 
        onAdd={addRecord}
        sort={sortBy}/>
      <Collection 
        records={records}
        removeRecord={removeRecord}/>
    </div>
  );
}

export default App;
