import React from 'react';
import Record from './Record';




function Collection ({records, removeRecord, artists}){

  const displayArtist = (id) => {
    console.log(id)
    let currentArtist = artists.find(artist => artist.artistID === id)
    try{
      return currentArtist.name;
    }catch(err){
      console.log(err)
    }

  }

  const displayLabel = (id) => {
    console.log(id)
    let currentLabel = artists.find(artist => artist.artistID === id)
    try{
      return currentArtist.name;
    }catch(err){
      console.log(err)
    }

  }

  return(
    <div className="Collection">
      {records.map((record, index) => (
        <Record 
          key={index}
          artist={displayArtist(record.artistID)}
          title={record.title}
          year={record.year}
          label={displayLabel(record.labelID)}
          size={record.size}
          onRemove={removeRecord}
        />
      ))}
    </div>
  )
}
export default Collection;
