import React, {Fragment} from 'react';

const InputPlusAutofill = ({term, list, onClickSuggestion, handleChange}) => {

  let suggestions = list.filter(string => string.toLowerCase().indexOf(term.toLowerCase()) > -1);

  return term && (
    <Fragment>
      <input 
              type="text"
              name="addTitle"
              value={term}
              onChange={e => handleChange(e.target.value)}
              placeholder="Title"/>  
      { suggestions.map((suggestion, index) => ( 
      <p 
        key={index} 
        className="suggestion" 
        onClick={e => handleChange(suggestion) }
      >
        { suggestion }
      </p>
      ))}
    </Fragment>
  )
}

export default InputPlusAutofill;
