import initialRecordState from './initialState';

const recordReducer = (state = initialState.currentRecord, action) => {
  switch(action.type){
    case 'CHANGE_TITLE':
     return state.currentRecord.title = action.payload;
    case 'CHANGE_ARTIST':
     return state.currentRecord.artist = action.payload;
    case 'CHANGE_YEAR':
     return state.currentRecord.year = action.payload;
    case 'CHANGE_LABEL':
     return state.currentRecord.label = action.payload;
    case 'CHANGE_SIZE':
     return state.currentRecord.size = action.payload;
    default: return state;
  }
}

export default recordReducer;
